mapper.update:
UPDATE trip.tbl_review
SET RV_DELFLAG = 1
WHERE rv_no = #{rv_no}

UPDATE trip.tbl_review
SET RV_REPLCNT = RV_REPLCNT + #{amount}
WHERE rv_no = #{rv_no} AND RV_DELFLAG = 0

mapper.select:
 select COUNTRY_NAME
	from trip.tbl_country
	where continent_name=#{continent_name}

mapper.update:
UPDATE trip.tbl_review
SET RV_LIKE_CNT = #{rv_like_cnt}
WHERE rv_no = #{rv_no}
